INSERT INTO texts VALUES(2150000,'翻滚比丢',replace(replace('①：只要这张卡在怪兽区域存在，「比丢」怪兽以外的怪兽变成守备表示，攻击力·守备力变成0\r\n②：让手卡的这张卡回到持有者卡组才能发动。选自己场上1张「比丢」卡回到持有者卡组，从卡组把1只「比丢」怪兽特殊召唤。这个效果在对方回合也能发动\r\n③：这张卡从场上离开时，场上的「比丢」怪兽以外的怪兽攻击力·守备力直到下个回合的结束时变成0\r\nDoItYourselfz','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2150001,'挤肚比丢',replace(replace('①：只要这张卡在怪兽区域存在，以下效果适用\r\n●超量怪兽的超量素材送去墓地\r\n●连接怪兽所连接区不能使用，那些区域的怪兽效果无效化，不能攻击宣言\r\n②：让手卡的这张卡回到持有者卡组才能发动。选自己场上1张「比丢」卡回到持有者卡组，从卡组把1只「比丢」怪兽特殊召唤。这个效果在对方回合也能发动\r\n③：这张卡从场上离开时，场上的连接怪兽所连接区的怪兽直到下个回合的结束时效果无效化，不能攻击宣言\r\nDoItYourselfz','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
